import{_ as t,c as d,o,a4 as e}from"./chunks/framework.I1cCAST3.js";const a="/assets/shortcuts.DGAW4g4t.png",p=JSON.parse('{"title":"快捷键设置","description":"","frontmatter":{},"headers":[],"relativePath":"docs/settings/shortcuts/index.md","filePath":"docs/settings/shortcuts/index.md"}'),r={name:"docs/settings/shortcuts/index.md"},l=e('<h1 id="快捷键设置" tabindex="-1">快捷键设置 <a class="header-anchor" href="#快捷键设置" aria-label="Permalink to &quot;快捷键设置&quot;">​</a></h1><p>快捷键设置用于配置和自定义快捷键，提高操作效率。在设置页面点击&quot;快捷键设置&quot;，可以查看所有功能的快捷键。</p><p><img src="'+a+'" alt=""></p><h2 id="常用快捷键" tabindex="-1">常用快捷键 <a class="header-anchor" href="#常用快捷键" aria-label="Permalink to &quot;常用快捷键&quot;">​</a></h2><h3 id="界面控制" tabindex="-1">界面控制 <a class="header-anchor" href="#界面控制" aria-label="Permalink to &quot;界面控制&quot;">​</a></h3><table tabindex="0"><thead><tr><th>功能</th><th>macOS</th><th>Windows/Linux</th></tr></thead><tbody><tr><td>打开设置</td><td><code>Command + ,</code></td><td><code>Ctrl + ,</code></td></tr><tr><td>切换左侧边栏</td><td><code>Command + B</code></td><td><code>Ctrl + B</code></td></tr><tr><td>切换右侧边栏</td><td><code>Command + Option + B</code></td><td><code>Ctrl + Alt + B</code></td></tr><tr><td>发送到 AI / 切换 AI 侧边栏</td><td><code>Command + L</code></td><td><code>Ctrl + L</code></td></tr><tr><td>切换布局（Terminal/Agents）</td><td><code>Command + E</code></td><td><code>Ctrl + E</code></td></tr><tr><td>切换 Agents 布局左侧边栏</td><td><code>Command + Shift + S</code></td><td><code>Ctrl + Shift + S</code></td></tr><tr><td>切换 AI 模式</td><td><code>Shift + Tab</code></td><td><code>Shift + Tab</code></td></tr></tbody></table><h3 id="终端操作" tabindex="-1">终端操作 <a class="header-anchor" href="#终端操作" aria-label="Permalink to &quot;终端操作&quot;">​</a></h3><table tabindex="0"><thead><tr><th>功能</th><th>macOS</th><th>Windows/Linux</th></tr></thead><tbody><tr><td>打开 AI 命令生成对话框</td><td><code>Command + K</code></td><td><code>Ctrl + K</code></td></tr><tr><td>打开文件管理</td><td><code>Command + M</code></td><td><code>Ctrl + M</code></td></tr><tr><td>清屏</td><td><code>Command + P</code></td><td><code>Ctrl + P</code></td></tr><tr><td>终端搜索</td><td><code>Command + F</code></td><td><code>Ctrl + F</code></td></tr><tr><td>复制</td><td><code>Command + C</code></td><td><code>Ctrl + C</code></td></tr><tr><td>粘贴</td><td><code>Command + V</code></td><td><code>Ctrl + V</code></td></tr></tbody></table><h3 id="标签页管理" tabindex="-1">标签页管理 <a class="header-anchor" href="#标签页管理" aria-label="Permalink to &quot;标签页管理&quot;">​</a></h3><table tabindex="0"><thead><tr><th>功能</th><th>macOS</th><th>Windows/Linux</th></tr></thead><tbody><tr><td>切换到下一个标签页</td><td><code>Control + Tab</code></td><td><code>Ctrl + Tab</code></td></tr><tr><td>切换到上一个标签页</td><td><code>Control + Shift + Tab</code></td><td><code>Ctrl + Shift + Tab</code></td></tr><tr><td>切换到指定标签页 [1-9]</td><td><code>Command + [1-9]</code></td><td><code>Ctrl + [1-9]</code></td></tr><tr><td>新 Tab 页</td><td><code>Command + N</code></td><td><code>Ctrl + N</code></td></tr></tbody></table><h3 id="字体控制" tabindex="-1">字体控制 <a class="header-anchor" href="#字体控制" aria-label="Permalink to &quot;字体控制&quot;">​</a></h3><table tabindex="0"><thead><tr><th>功能</th><th>macOS</th><th>Windows/Linux</th></tr></thead><tbody><tr><td>字体放大</td><td><code>Command + =</code></td><td><code>Ctrl + =</code></td></tr><tr><td>字体缩小</td><td><code>Command + -</code></td><td><code>Ctrl + -</code></td></tr></tbody></table><h2 id="自定义快捷键" tabindex="-1">自定义快捷键 <a class="header-anchor" href="#自定义快捷键" aria-label="Permalink to &quot;自定义快捷键&quot;">​</a></h2><p>在快捷键设置页面，可以为常用功能设置自定义快捷键：</p><ol><li>找到要自定义的功能项</li><li>点击快捷键输入框</li><li>按下您想要设置的快捷键组合</li><li>系统会自动检测快捷键冲突</li><li>保存设置</li></ol><div class="tip custom-block"><p class="custom-block-title">提示</p><ul><li>自定义快捷键会覆盖默认快捷键</li><li>如果快捷键已被其他功能使用，系统会提示冲突</li><li>建议选择不常用的快捷键组合，避免与系统快捷键冲突</li></ul></div><h2 id="快捷键重置" tabindex="-1">快捷键重置 <a class="header-anchor" href="#快捷键重置" aria-label="Permalink to &quot;快捷键重置&quot;">​</a></h2><p>在快捷键设置页面，可以重置全部快捷键到默认值：</p><ol><li>点击&quot;重置全部快捷键&quot;按钮</li><li>确认重置操作</li><li>所有自定义快捷键将被清除，恢复为默认值</li></ol><div class="warning custom-block"><p class="custom-block-title">注意</p><p>重置操作不可恢复，请谨慎操作。</p></div><h2 id="使用建议" tabindex="-1">使用建议 <a class="header-anchor" href="#使用建议" aria-label="Permalink to &quot;使用建议&quot;">​</a></h2><h3 id="学习技巧" tabindex="-1">学习技巧 <a class="header-anchor" href="#学习技巧" aria-label="Permalink to &quot;学习技巧&quot;">​</a></h3><ul><li><strong>逐步学习</strong>：从最常用的快捷键开始，逐步掌握更多快捷键</li><li><strong>定期练习</strong>：每天花几分钟练习快捷键，形成肌肉记忆</li><li><strong>个性化</strong>：根据个人使用习惯自定义快捷键设置</li><li><strong>文档记录</strong>：记录个人常用的快捷键组合，方便查阅</li></ul><h3 id="快捷键冲突" tabindex="-1">快捷键冲突 <a class="header-anchor" href="#快捷键冲突" aria-label="Permalink to &quot;快捷键冲突&quot;">​</a></h3><p>如果快捷键不生效，可能的原因：</p><ul><li>系统快捷键冲突</li><li>快捷键设置未保存</li><li>应用焦点问题</li></ul><p><strong>解决方案</strong>：</p><ol><li>检查系统快捷键设置，避免冲突</li><li>重新设置快捷键并保存</li><li>确保应用处于活动状态</li></ol>',28),c=[l];function i(n,h,s,m,b,u){return o(),d("div",null,c)}const _=t(r,[["render",i]]);export{p as __pageData,_ as default};
